INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (2, 8, 'Stir when you have time during cooking.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (2, 9, 'Cook until beans are tender, about 16 hours.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (5, 11, 'Slice beef and serve with sauce and vegetables.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (7, 6, 'Bake at 375 degrees for about 10 minutes, or until cookies are firm and lightly browned.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (7, 7, 'Remove cookies and place them on a wire rack.  Allow the cookies to reach room temperature before serving.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (1, 1, 'Place potatoes in large pot & add enough cold water to cover by 2 inches. Bring potatoes to boil over high heat, then lower heat to maintain gentle simmer. Cook potatoes until they are barely tender.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (1, 0, 'Adjust oven rack to middle position & preheat oven to 350 degrees.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (3, 2, 'Pour over ice and add your favorite ingredients.  Sit back, relax, and enjoy!  Note: (This keeps well & gets better with age.)');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (8, 5, 'Fold cream into egg yolk mixture, then fold in whites.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (8, 6, 'Serve in cups with nutmeg on top.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (9, 7, 'Refrigerate for later use.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (9, 8, 'If starter has not been used for 10 days, stir in 1 tsp sugar or honey. Repeat any 10 day period when starter is not used.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (10, 9, 'Bake loaves 35 - 40 minutes. Cool on wire racks.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (11, 10, 'Bake loaves 35 - 40 minutes until it makes a hollow sound when you thump the top.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (11, 11, 'Brush tops with butter & cool on a wire rack.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (12, 2, 'Dissolve the yeast in 2/3 cup warm water and add the reserved teaspoon honey. Add the cooled cracked wheat mixture. Stir in flour and mix thoroughly.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (12, 1, 'Add the honey, reserving 1 teaspoon for later, salt and oil. Let cool.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (12, 0, 'Pour 2 cups boiling water over the cracked wheat.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (13, 1, 'Mix 2 1/4 c water, olive oil, flour, and salt with yeast mixture.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (13, 0, 'Mix 1/4 cup water & yeast. Allow to stand for 10 minutes.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (18, 7, 'Note:  Instead of the coarse salt, you can sprinkle pretzels with cinammon and sugar OR you can sprinkle pretzels with parmesan cheese and coarse salt.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (18, 6, 'Bake pretzels for about 10 minutes or until golden brown.  Take out of the oven and brush with melted butter & sprinkle with coarse salt.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (29, 4, 'Add the dried fruits in the last 5 minutes of roasting. Cool completely and store in an airtight container.  ');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (29, 3, 'Spread on the baking sheet.  Bake for 25 to 30 minutes , stiring every 10 minutes, until the mixture is roasted evenly.  ');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (29, 2, 'In a separate bowl, mix the honey with the oil and vanilla.  Pour oil mixture over the oatmeal mixture and stir with a large spatula.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (29, 1, 'Mix oatmeal, coconut, nuts, wheat germ, seeds, and cinnamon in a a large bowl.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (29, 0, 'Preheat oven to 325 degrees.  Lightly oil a banking sheet with canola oil using a pump spray canister.  ');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (30, 1, 'Mix dry ingredients in a large bowl.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (30, 2, 'In another bowl, wisk eggs, pumpkin, and butter until well blended.    Stir in chocolate chips and pour over dry ingredients.  Stir until just moistened.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (30, 0, 'Preheat oven to 350 degrees.  Grease muffin tin with oil.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (31, 2, 'Place 4 cups of lentils in a blender or food processor and process until smooth. Return mixture to crockpot and add the basil, vinegar, salt, and tomatoes.  Cook until thoroughtly heated.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (31, 0, 'Heat oil in skillet over medium-high heat.  Add onions; saute 4 minutes.  Add curry, saute 1 minute.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (31, 1, 'Place onion in a crockpot and add water, chicken broth and lentils.  Cook on high 3 to 4 hours or low 7 to 8 hours.  Make sure you check the lentils for tenderness as cooking times will vary depending on your make and model of crockpot.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (33, 0, 'Preheat oven to 350 degrees.  Lightly coat an 8 X 4\\\\\\" loaf pan with canola oil and set aside.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (33, 1, 'In a large bowl, combine the pastry flour, all-purpose flour, flaxseed, brown sugar, baking soda and salt, and set aside. ');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (33, 2, 'In a medium bowl, lightly beat the eggs.  Whisk in the pumpkin, canola oil, applesauce, apple juice, cinnamon, ginger and nutmeg.  Stir in the dried cranberries.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (33, 3, 'Add the wet ingredients to the dry ingredients, mixing until all the dry ingredients are fully incorporated into the batter.  Do not beat or overmix.  Pour the batter into the prepared pan.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (33, 4, 'Bake for 50 to 60 minutes, until a wooden toothpick inserted into the center comes out clean. Cool in the pan on a wire rack for 10 minutes.  Remove the bread from the pan and continue cooling on the rack.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (36, 1, 'In a small saucepan, blanch broccoli in salted boiling water.  Drain.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (36, 2, 'While fettuccine is cooking, cook onions, garlic, and mushrooms in the butter in a heavy saucepan until onions are translucent.  Stir in flour and cook over low heat for 5 mintues, stirring constantly.  ');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (36, 3, 'Add milk, chicken bouillon, and nutmeg, stirring continually until mixture thickens.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (36, 4, 'Add cream cheese and cooked broccoli, stirring over low heat just until cream cheese is incorporated and mixture is hot.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (36, 5, 'Add fettuccine noodles, cheese, parsley, and pepper to taste.  Serve hot with crusty garlic bread.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (36, 6, 'Note:  This recipe is also good made with shrimp or chicken.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (36, 0, 'In a large saucepan, cook fettuccine in salted water until tender.  Drain.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (38, 0, 'In a large pot, combine chickepeas with 8 cups of water.  Cover, and brind to a boil over high heat.  Reduce heat to medium, and cook, uncovered, 25 minutes.  Remove from heat.  Let stand, covered, in water overheat.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (38, 1, 'Place sun-dried tomoatoes in shallow bowl; pour enough boiling water to cover.  Let stand 20 minutes.  Drain, chop coarsley, set aside.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (38, 2, 'Drain soaked chickpeas and ransfer to food processor.  Add 2 tablespoons oil, 1 tsp. salt and 1/2 tsp. black pepper and process until finely chopped. Add reserved tomatoes, parsley, mint and soy sauce, and process briefly to combine.  Scrape mixture into bowl.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (38, 3, 'In a large skillet, heat remaining 1 tablespoon oil over medium heat.  Add onion, and cook, stirring often, until softened, about 6 minutes.  Stir in cumin, 1/2 teaspoon salt, garlic and jalapeno, and cook, stirring often, 5 mintues.  Add onion mixture to chickpeas.  Stir in flour until well blended.  Form mixture into 8 round patties.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (38, 4, 'Preheat electric grill to 350 degrees, or heat a grill pan over medium heat.  Cook burgers, turning once, until golden brown, 4 to 5 minutes.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (39, 1, 'In a large bowl, dump the cereal, raisins and oil and pour the \r\nboiling water right over the top. Give it a stir or two and set \r\naside.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (39, 2, 'In another bowl, combine the buttermilk, molasses, honey, eggs \r\nand rind. Pour that mess over the top of your warm cereal \r\nmixture, stirring to combine.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (39, 3, 'In yet another bowl, combine dry ingredients -- flour, baking \r\nsoda, salt and add to the cereal mixture. ');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (39, 4, 'Line your muffin tins with cupcake liners and fill 2/3 full with \r\nbatter. ');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (39, 5, 'Bake for 20 to 25 minutes.  ');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (39, 6, 'Cool in tins for a few \r\nminutes then carefully lift them out to continue cooling on a rack.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (54, 5, 'Periodically stir with a fork as it freezes to create slush.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (39, 0, 'Preheat oven to 400 degrees.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (41, 0, 'In a large bowl, combine 1 cup flour, sugar, salt, and dry yeast. ');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (41, 1, 'Add milk and softened butter or margarine. ');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (41, 2, 'Stir in starter. ');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (41, 3, 'Mix in up to 3 3/4 cups flour gradually, you may need more depending on your climate.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (41, 4, 'Knead 8 to 10 minutes.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (41, 5, 'Turn dough out onto a floured surface, and knead for 8 to 10 minutes.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (41, 6, 'Place in a greased bowl, turn once to oil surface, and cover. Allow to rise for 1 hour, or until doubled in volume.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (41, 7, 'Punch down, and let rest 15 minutes.\r\n');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (41, 8, 'Shape into loaves. Place on a greased baking pan. Allow to rise for 1 hour, or until doubled.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (41, 9, 'Brush egg wash over tops of loaves, and sprinkle with chopped onion.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (41, 10, 'Bake at 375 degrees F (190 degrees C) for 30 minutes, or till done.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (42, 0, 'Lightly spoon all-purpose flour into dry measuring cups, level with a knife.  Combine flour and next 5 ingredients (through cinnamon) in a large bowl, stirring with a wisk.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (42, 1, 'Combine milk, sweet potatoes, sugar, oil, vanilla, and egg yolks, stirring until smooth; add to flour mixture, stirring just until combined,  Beat egg whites with mixerat high speed until soft peaks form; fold egg whites into batter.  Let batter stand 10 minutes.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (42, 2, 'Heat a nonstick griddle or a nonstick skillet over medium-high heat.  Coat the griddle or pan with with cooking spray.  Spoon about 1/4 cup batter per pancake onto griddle or pan.  Cook 2 minutes or until tops are covered with bubbles and edges are cooked.  Carefully turn pancakes over, and cook 2 minutes or until bottoms are lightly browned.  ');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (43, 0, 'Combine yogurt, juice, garlic, 1/4  teaspoon salt, 1/4 teaspoon pepper, cardamom, cumin, cinnamon, and nutmeg in a large zip-top plastic bag.  Add chicken, seal and marinate in refrigerator overnight, turning bag occasionally.\r\n');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (43, 1, 'Remove chicken from bag, reserving marinade; pat chicken dry with paper towels.  Combine reserved marinade and chicken broth.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (43, 2, 'Heat olive oil in a large nonstick skillet over medium-high heat.  Sprinkle chicken with remaining 1/4 teaspoon salt and remaining 1/4 teaspoon pepper.  Add half of chicken to pan; cook 1 1/2 minutes on each side or until browned.  Repeat procedure with remaining chicken.  Return chicken to pan; ppour broth mixture over chicken.  Bring to a boil.  Cover, reduce heat, and simmer 12 minutes or until chicken is done.  Place chicken on serving platter; discard liquid in pan.  Sprinkle chicken evenly with parsley.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (43, 3, 'Note:  This dish is excellent served with a basmati rice pilaf of toasted almonds, golden reasins, and freshly toasted cumin seeds.  I also like to spoon a little of the sauce in the pan onto the rice for added moisture.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (30, 3, 'Bake for 20 to 25 minutes or until muffins are springy to touch.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (44, 0, 'Soak rice noodles in warm water until soft, drain.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (44, 1, 'Heat a frying pan until hot and add oil.  Add garlic and brown slightly.  Add egg and scramble until done.  Reduce temperatue if necessary.');
INSERT INTO `steps` (`recipe_id`, `order_num`, `step_descr`) VALUES (44, 2, 'Add rice noodles to egg mixture.  Turn temperature up and stir-fry noodles until translucent, adding some water if the noodles are dry.');
